import React, { Component } from 'react';
import Axios from 'axios';
class AddClient extends Component {
    constructor(){
        super()
        this.state = {
            firstName: "",
            surname: "",
            country: "",
            owner: "",
        }
    }

    addClient = () => {

        Axios.post('http://localhost:5000/addClient', {name: this.state.firstName + " " + this.state.surname, country: this.state.country, owner: this.state.owner })
        this.setState({firstName: "", surname:"", country:"", owner:""})
    }
    update = async (event) => {
        await this.setState({
            [event.target.name]: event.target.value,
        })

    }
    render() {



        return (

            <div>
                ADD CLIENT
                <div>First name: <input type="text" name="firstName" value={this.state.firstName} onChange={this.update}/></div>
                <div>Surname: <input type="text" name="surname" value={this.state.surname} onChange={this.update}/></div>
                <div>Country: <input type="text" name="country" value={this.state.country} onChange={this.update}/></div>
                <div>Owner: <input type="text" name="owner" value={this.state.owner} onChange={this.update}/></div>
                <button onClick={this.addClient}>Add</button>

            </div>
        )
    }
}
export default AddClient;
