import React, { Component } from 'react';
import './App.css';
import NavBar from './components/NavBar';
import axios from 'axios'
import EditClient from './components/routes/clients/EditClient'


import { BrowserRouter as Router, Route } from 'react-router-dom'
import Clients from './components/routes/clients/Clients';
import Analytics from './components/routes/Analytics';
import Actions from './components/routes/Actions';
import { totalmem } from 'os';

class App extends Component {

  constructor() {
    super()
    this.state = {


    }
  }

  async componentDidMount() {


   let data = await axios.get('http://localhost:5000/clients')
   this.setState({ data: data.data }) 
   this.setState({length:  Math.ceil(this.state.data.length / 20)})

  }


  render() {

    return (

        <Router>
         <NavBar />
          <Route exact path="/clients" render={() => <Clients length={this.state.length} clients={this.state.data} />} />
          <Route exact path="/actions" render={() => <Actions />} />
          <Route exact path="/analytics" render={() => <Analytics />} />
          <Route path="/edit" render={() => <EditClient/>  } />

        </Router>
    )
  }
}


export default App;
