class EditBox extends Component {
    render() {
        return (<div id="EditBox">
            <div onC>X</div>
            <div>Name: <input type="text" placeholder={this.props.client.name.split(" ")[0]}/></div>
            <div>Surname: <input type="text" placeholder={this.props.client.name.split(" ")[1]}/></div>
            <div>Country: <input type="text" placeholder={this.props.client.country}/></div>
            <button>Update</button>
        </div>)
    }
}
export default EditBox;
