import React, { Component } from 'react';
import { LineChart, Line } from 'recharts';
import {  Tooltip, Legend, CartesianGrid, XAxis, YAxis, ResponsiveContainer } from 'recharts';



class SalesSince extends Component {

    render() {
        const monthNames = ["January", "February", "March", "April", "May", "June",
            "July", "August", "September", "October", "November", "December"
        ]
        let today = new Date()

        let sales = (
            <ResponsiveContainer width='100%' height={300}><BarChart data={this.state.countries} >
                <Bar dataKey="sales" fill="#8884d8" stroke="#8884d8" barSize={30} />
                <XAxis type="category" dataKey="country" />
                <YAxis   type="number"/>
                <Tooltip cursor={{opacity: 0.5}} />

            </BarChart></ResponsiveContainer>)

        return (<div>
            Sales since  {monthNames[today.getMonth() - 1]}  {today.getDate()}
            {sales}
        </div>)
    }
}
export default SalesSince;
