import React, { Component } from 'react';
import Client from './Client';
import ClientsHeader from './ClientsHeader';
import ClientsPages from './ClientsPages';
import EditBox from './EditBox';
import axios from 'axios';



class Clients extends Component {
    constructor(){
        super()
        this.state = {
            pages: {
                min: 0,
                max: 19,
                actual: 1,
               },
               edit: false,
               editThis:{}
        }
    }
changeClient = (client) =>{
  this.setState({edit: false})
  this.setState({edit: true})
  this.setState({editThis: client})
}
closeChangeClients = () =>{
  this.setState({edit: false})
}
sendChangeClient = (newInfo) =>{
  axios.put(`http://localhost:5000/editclient/${this.state.editThis.email}`, (newInfo))
  console.log(newInfo)

  this.setState({edit: false})
}

    changePages = bool =>{
        if( bool && this.state.pages.actual < this.props.length){
          let pages = {...this.state.pages}
          pages.min = this.state.pages.min + 20
          pages.max = this.state.pages.max + 20
          pages.actual = this.state.pages.actual + 1
          this.setState({pages: pages})
        }
        else if(!bool && this.state.pages.min > 0 ){
          let pages = {...this.state.pages}
          pages.min = this.state.pages.min - 20
          pages.max = this.state.pages.max - 20
          pages.actual = this.state.pages.actual - 1
          this.setState({pages: pages})
        }
      }

    render() {

        let clients = this.props.clients.filter((c, i) => i >= this.state.pages.min && i <= this.state.pages.max )


        return (<div>
           {this.state.edit ? <EditBox send={this.sendChangeClient} close={this.closeChangeClients} client={this.state.editThis}/> : null}
            <ClientsHeader/>
            {clients.map((c, i) =>  <Client  changeClient={this.changeClient} client={c}/> )}
            <ClientsPages pages={this.state.pages} length={this.props.length} changePages={this.changePages} />
        </div>)
    }
}
export default Clients;
