import React, { Component } from 'react';
class EditBox extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: props.client.name.split(" ")[0],
            surname: props.client.name.split(" ")[1],
            country: props.client.country
        }
    }

    update = async (event) => {
        await this.setState({
            [event.target.name]: event.target.value,
        })
    }


    changeClient = () => {


        this.props.send({
            name: this.state.name + " " + this.state.surname,
            country

        })
    }

    render() {
        return (<div id="EditBox">
            <div onClick={this.props.close}>X</div>
            <div>Name: <input name="name" value={this.state.name} onChange={this.update} type="text" /></div>
            <div>Surname: <input name="surname" value={this.state.surname} onChange={this.update} type="text"  /></div>
            <div>Country: <input name="country" value={this.state.country} onChange={this.update} type="text"  /></div>
            <button onClick={this.changeClient}>Update</button>
        </div>)
    }
}
export default EditBox;
