    render() {
        let ownersobj = {}
        this.props.clients.forEach(d => {
            if (!ownersobj[d]) {
                owners.push(d)
                ownersobj[d] = true

            }
        })


        return (<div>
            UPDATE CLIENT
            <div>Client: <input type="text" name="" placeholder="Client name" /></div>
            <div>Trasfer ownership to:
                 <select>
                    {owners.map(o => <option value={o}>{o}</option>)}

                </select>
                <button>Transfer</button>
            </div>

            <div>Send email:
                    <select>
                         <option value="A">A</option>
                         <option value="B">B</option>
                        <option value="C">C</option>
                        <option value="D">D</option>
                     </select>

                <button>Send</button>
            </div>
            <div>Declare sale <button>Declare</button></div>

        </div>)
    }

}

export default UpdateClient;
