import React, { Component } from 'react';
import TopEmployees from './TopEmployees'

class Charts extends Component {
    constructor() {
        super(props)
        this.state = {
        }

    }

    componentDidMount(){
        let topEmployees = []
        let emp = {}
        let employees = []
        this.props.clients.forEach(c => {
            if (emp[c.owner]) {
                emp[c.owner]+= 1
            }
            else{
                emp[c.owner] = 1
                employees.push(c.owner)
            }
        })
        employees.forEach(e => topEmployees.push({name: e, sales: emp[e]}))
        employees.sort((a,b) => (a.sales > b.sales) ? 1 : ((b.sales > a.sales) ? -1 : 0))
        employees.splice(3)
        this.setState({topEmployees: employees})
    }
    render() {
        return (<div>
            <TopEmployees topEmployees={this.state.topEmployees}/>
        </div>)
    }
}
export default Charts;
