import React, { Component } from 'react';
import { BarChart, Bar } from 'recharts';
import { LineChart, Line, Tooltip, Legend, CartesianGrid, XAxis, YAxis, ResponsiveContainer } from 'recharts';

class TopEmployees extends Component {
    constructor() {
        super()
        this.state = {
            topEmployees: [{ name: "Jorge", sales: 40 }, { name: "Maria", sales: 20 }, { name: "Laura", sales: 50 }]
        }

    }
    componentDidMount() {
        let topEmployees = []
        let emp = {}
        let employees = []
        this.props.clients.forEach(c => {
            if (emp[c.owner]) {
                emp[c.owner]+= 1
            }
            else{
                emp[c.owner] = 1
                employees.push(c.owner)
            }
        })
        employees.forEach(e => topEmployees.push({name: e, sales: emp[e]}))
        employees.sort((a,b) => (a.sales > b.last_nom) ? 1 : ((b.last_nom > a.last_nom) ? -1 : 0))
    }

    render() {

        const renderLineChart = (
            <ResponsiveContainer width='100%' height={300}><BarChart data={this.state.topEmployees} layout='vertical'>
                <Bar dataKey="sales" fill="#8884d8" stroke="#8884d8" barSize={30} />
                <XAxis type="number" />
                <YAxis type="category" dataKey="name" />
                <Tooltip Style={{ opacity: 0.5 }} />

            </BarChart></ResponsiveContainer>)


        return (<div> Top Employees
            {renderLineChart}
        </div>)
    }
}
export default TopEmployees;
