import React, { Component } from 'react';
import './App.css';
import NavBar from './components/NavBar';


import { BrowserRouter as Router, Route } from 'react-router-dom'
import Clients from './components/Clients';
import Analytics from './components/Analytics';
import Actions from './components/Actions';

class App extends Component {

  constructor() {
    super()
    this.state = {
     pages: {
      min: 0,
      max: 20,
     }

    }
  }

   componentDidMount() {
   setTimeout(() => {
      let data = require('./data.json')
      this.setState({ data: data })
    }, 1000)
  }

  changePages(bool){
    if(bool && this.pages.min >0 ){
      let pages = [...this.state.pages]
      pages.min = this.pages.min + 20
      pages.max = this.pages.min + 20
      this.setState({pages: pages})
    }
    else{
      let pages = [...this.state.pages]
      pages.min = this.pages.min - 20
      pages.max = this.pages.min - 20
      this.setState({pages: pages})
    }
  }
  render() {

    console.log(this.state.data)

    return (





        <Router>
         <NavBar />
          <Route exact path="/clients" render={() => <Clients clients={this.state.data} pages={this.state.pages} changePages={this.props.changePages}/>} />
          <Route exact path="/actions" render={() => <Actions />} />
          <Route exact path="/analytics" render={() => <Analytics />} />

        </Router>
    )
  }
}


export default App;
