import React, { Component } from 'react';
class UpdateClient extends Component {
    constructor(){
        super()
        this.state={
            owners:[],
            name:""
        }
    }

    componentDidMount(){
        console.log(this.props.clients)
        let ownersobj = {}
        this.props.clients.forEach(d => {
            if (!ownersobj[d.owner]) {
                this.state.owners.push(d.owner)
                ownersobj[d.owner] = true

            }
        })
    }

    update = async (event) => {
        await this.setState({
            [event.target.name]: event.target.value,
        })
    }
    render() {



        return (<div>
            UPDATE CLIENT
            look for client
            <div>Client: <input type="text" name="name" value={this.state.name} onChange={this.update} placeholder="Client name" /></div>
            <div>Trasfer ownership to:
                 <select>
                    {this.state.owners.map(o => <option value={o}>{o}</option>)}

                </select>
                <button >Transfer</button>
            </div>

            <div>Send email:
                    <select>
                         <option value="A">A</option>
                         <option value="B">B</option>
                        <option value="C">C</option>
                        <option value="D">D</option>
                     </select>

                <button>Send</button>
            </div>
            <div>Declare sale <button>Declare</button></div>

        </div>)
    }

}

export default UpdateClient;
