import React, { Component } from 'react';
import './App.css';
import NavBar from './components/NavBar';


import { BrowserRouter as Router, Route } from 'react-router-dom'
import Clients from './components/Clients';
import Analytics from './components/Analytics';
import Actions from './components/Actions';
import { totalmem } from 'os';

class App extends Component {

  constructor() {
    super()
    this.state = {
     pages: {
      min: 0,
      max: 19,
      actual: 1,


     }

    }
  }

   componentDidMount() {
   setTimeout(() => {
      let data = require('./data.json')
      this.setState({ data: data })
    }, 1000)
  }

  changePages = bool =>{
    if(bool && this.state.pages.max < Math.ceil(this.state.data.length / 20)){
      let pages = {...this.state.pages}
      pages.min = this.state.pages.min + 20
      pages.max = this.state.pages.max + 20
      pages.actual = this.state.pages.actual + 1
      this.setState({pages: pages})
    }
    else if(!bool && this.state.pages.min > 0 ){
      let pages = {...this.state.pages}
      pages.min = this.state.pages.min - 20
      pages.max = this.state.pages.max - 20
      pages.actual = this.state.pages.actual - 1
      this.setState({pages: pages})
    }
  }
  render() {




    return (





        <Router>
         <NavBar />
          <Route exact path="/clients" render={() => <Clients clients={this.state.data} pages={this.state.pages} changePages={this.changePages}/>} />
          <Route exact path="/actions" render={() => <Actions />} />
          <Route exact path="/analytics" render={() => <Analytics />} />

        </Router>
    )
  }
}


export default App;
