import React, { Component } from 'react';
import Client from './Client';
import ClientsHeader from './ClientsHeader';
import ClientsPages from './ClientsPages';


class Clients extends Component {
    render() {
        let pages = this.props.pages
        let clients = this.props.clients.filter((c, i) => i >= pages.min && i <= pages.max )
        let totalpages = Math.ceil(this.props.clients.length / 20)

        return (<div>
            <ClientsHeader/>
            {clients.map((c, i) =>  <Client client={c}/> )}
            <ClientsPages pages={this.props.pages} totalpages={totalpages} changePages={this.props.changePages} />
        </div>)
    }
}
export default Clients;
