import React, { Component } from 'react';
import Client from './Client';
import ClientsHeader from './ClientsHeader';
import ClientsPages from './ClientsPages';


class Clients extends Component {
    render() {
        let pages = this.props.pages
        let clients = this.props.clients.filter((c, i) => i)
        return (<div>
            <ClientsHeader/>
            {this.props.clients.map((c, i) => i >= pages.min && i <= pages.max ? <Client client={c}/> : null)}
            <ClientsPages />
        </div>)
    }
}
export default Clients;
