import React, { Component } from 'react';
import { PieChart, Pie } from 'recharts';
import {  Tooltip, Legend, CartesianGrid, XAxis, YAxis, ResponsiveContainer } from 'recharts';


class ClientAqcuisition extends Component {
    constructor(props) {
        super(props)
        this.state = {
            periods: [
                { period: "> 12 Mons ago", amount: 508 },
                { period: "6-12 Mons ago", amount: 200 },
                { period: "Last 6 Mons", amount: 308 },
                { period: "last Mon", amount: 65 },

            ]
        }

    }

    render() {
        let chart =
        (
        <ResponsiveContainer width='100%' > <PieChart width={730} height={250}>
            <Pie data={this.state.periods} dataKey="amount" nameKey="period" cx="50%" cy="50%" outerRadius={50} fill="#8884d8" />
        </PieChart> </ResponsiveContainer>)

        return (<div>
            {chart}
        </div>)
    }
}
export default ClientAqcuisition;
