import React, { Component } from 'react';
import { BarChart, Bar } from 'recharts';
import {  Tooltip, Legend, CartesianGrid, XAxis, YAxis, ResponsiveContainer } from 'recharts';

class TopEmployees extends Component {
    constructor(props) {
        super(props)
        this.state = {
            topEmployees: []
        }

    }
    // componentDidMount() {
