    //     employees.splice(3)
    //     this.setState({topEmployees: employees})
    // }


    render() {

        const renderLineChart = (
            <ResponsiveContainer width='100%' height={300}><BarChart data={this.state.topEmployees} layout='vertical'>
                <Bar dataKey="sales" fill="#8884d8" stroke="#8884d8" barSize={30} />
                <XAxis type="number" />
                <YAxis type="category" dataKey="name" />
                <Tooltip Style={{ opacity: 0.5 }} />

            </BarChart></ResponsiveContainer>)


        return (<div> Top Employees
            {renderLineChart}
        </div>)
    }
}
export default TopEmployees;
