import React, { Component } from 'react';

class HottestCountry extends Component {
    render() {
        let countries = {}
        let counter = 0
        let country = ""

        this.props.clients.forEach(c =>  {
            countries[c.country] ?  countries[c.country] += 1 : countries[c.country] = 1

            if(countries[c.country] > counter){
                country = c.country
                counter = countries[c.country]

            }
        })

        return (<div>
            Hottest Country: {country}
        </div>)
    }
}
export default HottestCountry;
