import React, { Component } from 'react';
import { BarChart, Bar } from 'recharts';
import {  Tooltip, Legend, CartesianGrid, XAxis, YAxis, ResponsiveContainer } from 'recharts';

class TopEmployees extends Component {
    constructor(props) {
        super(props)
        this.state = {
            topEmployees: this.props.topEmployees
        }

    }
    UNSAFE_componentWillReceiveProps(nextProps) {
        // Any time props.email changes, update state.
        if (nextProps.topEmployees !== this.props.topEmployees) {
          this.setState({
            topEmployees: nextProps.topEmployees
          });
        }
      }

    render() {

        let renderLineChart = (


            <ResponsiveContainer width='100%' height={300}><BarChart  width='100%' height={300} data={this.state.topEmployees} layout='vertical'>
                <Bar dataKey="sales" fill="#8884d8" stroke="#8884d8" barSize={30} isAnimationActive={true} />

                <Tooltip pointerEvents='hover'  cursor={{opacity: 0.5}}/>
                <Legend/>
                <XAxis type="number" />
                <YAxis type="category" dataKey="name" />

            </BarChart></ResponsiveContainer>)


        return (<div id="topEmployees"> Top Employees
            {renderLineChart}
        </div>)
    }
}
export default TopEmployees;
