import React, { Component } from 'react';
import Clients from './Clients';
import EditBox from './EditBox';


class Client extends Component {
    constructor() {
        super()
        this.state = {
            edit: false,
        }
    }
    edit = () => {
        this.setState({edit: false})
        this.setState({edit: false})
    }
    render() {
        let client = this.props.client

        return (
            <div>

                {this.state.edit ? <EditBox client={client}/> : null}
                <div onClick={this.edit} className="client">
                    <div>{client.name.split(" ")[0]}</div>
                    <div>{client.name.split(" ")[1]}</div>
                    <div>{client.country}</div>
                    <div>{client.firstContact.replace(/^([0-9]*-[0-9]*-[0-9]*).*$/, '$1')}</div>
                    <div>{client.emailType}</div>
                    <div>{client.sold ? "sold" : "not sold"}</div>
                    <div>{client.owner}</div>
                </div>

            </div>
        )
    }
}
export default Client;
