import React, { Component } from 'react';
import { BarChart, Bar } from 'recharts';
import { LineChart, Line, Tooltip, Legend, CartesianGrid, XAxis, YAxis } from 'recharts';

class TopEmployees extends Component {
    constructor(props) {
        super(props)
        this.state = {
            topEmployees: [{ name: "Jorge", sales: 40 }, { name: "Maria", sales: 20 }, { name: "Laura", sales: 50 }]
        }

    }


    render() {

        const renderLineChart = (
            <BarChart 
  width={600} 
  height={300} 
  data={this.state.topEmployees} 
  layout="horizontal"
  margin={{top: 5, right: 30, left: 20, bottom: 5}}
>
  <XAxis/>
  <YAxis type="category" dataKey="name" />
  <CartesianGrid strokeDasharray="3 3"/>
  <Tooltip/>
  <Legend />
  <Bar dataKey="sales" fill="#8884d8" />
</BarChart>
            )


        return (<div>
            {renderLineChart}
        </div>)
    }
}
export default TopEmployees;
