import React, { useState } from 'react';
import axios from 'axios'
import './header.css'
import { BrowserRouter as Router, Route } from 'react-router-dom'


function Header() {
    const [burger, setBurger] = useState(false)
    const [focus, setFocus] = useState(false)
    const [blur, setBlur] = useState(true)


    function focusFunc(boolean) {
        if (boolean) {
            setFocus(true)
            setBlur(false)
        }
        else {
            setFocus(false)
            setBlur(true)
        }
    }



    return (
        <div id="header-cont">

            <div id="header-inner">
                <div id="header-menu">
                        <div id="burger-cont" onClick={() => setBurger(false)}>
                            <div id="nav-icon4" className="open">  <span></span> </div>
                            <span id="header-menu-title">CERRAR</span>
                        </div> 
                    <div>
                        <input id="search" type="text" placeholder="Buscar" onFocus={() => { focusFunc(true) }} onBlur={() => focusFunc(false)} />
                    </div>
                </div>

                {blur ? <div id="logo-cont">  <a href="https://www.lanacion.com.ar/"><img id="logo" src={require('../../assets/lanacion.png')} /></a>  </div> : null}

                <div id="button-cont">
                    <div>
                        <button id="user-sub" className="user-butt">SUSCRIBITE</button>
                        <button id="user-ing" className="user-butt">INGRESAR</button>
                    </div>
                </div>
            </div>

        </div>
    )
}


export default Header;
