import React, { Component } from 'react';
import TopEmployees from './TopEmployees'
import SalesByCounty from './SalesByCountry';
import SalesSince from './SalesSince';

class Charts extends Component {
    constructor() {
        super()
        this.state = {
        }

    }
    topEmployees(){
        let topEmployees = []
        let emp = {}
        let employees = []
        this.props.clients.forEach(c => {
            if (emp[c.owner]) {
                emp[c.owner]+= 1
            }
            else{
                emp[c.owner] = 1
                employees.push(c.owner)
            }
        })
        employees.forEach(e => topEmployees.push({name: e, sales: emp[e]}))
        topEmployees.sort((a,b) => (a.sales < b.sales) ? 1 : ((b.sales < a.sales) ? -1 : 0))
        topEmployees.splice(3)
        return topEmployees

    }
    salesByCountry(){
        let salesCountries = []
        let sales = {}
        let countries = []

        this.props.clients.forEach(c => {
            if (sales[c.country]) {
                sales[c.country]+= 1
            }
            else{
                sales[c.country] = 1
                countries.push(c.country)
            }
        })
        countries.forEach(e => salesCountries.push({country: e, sales:  sales[e]}))
        return salesCountries
    }
    salesSince(){
        let today = new Date()
        let date = 2017 + '-' + 7 + '-' + today.getDate() // year at 2017 cause data is old
        let maxdate = 2017 + '-' + 8 + '-' + today.getDate()

        let salesByDate = []
        let datesCount = {}
        let dates = []

        this.props.clients.forEach(c => {
            if(c.firstContact > date && c.firstContact < maxdate ){
              if (datesCount[c.firstContact]) {
                datesCount[c.firstContact]+= 1
            }
            else{
                datesCount[c.firstContact] = 1
                dates.push(c.firstContact)
            }  
            }

        })
        dates.forEach(e => {salesByDate.push({date: e, sales: datesCount[e]})})
        return salesByDate.sort((a,b) => (a.date > b.date) ? 1 : ((b.date > a.date) ? -1 : 0))
    }
    render() {

    let employees= this.topEmployees()
    let countries = this.salesByCountry()
    let salesSince = this.salesSince()
    // console.log(salesSince)


        return (<div>
            <TopEmployees topEmployees={employees}/>
            <SalesByCounty countries={countries}/>
            <SalesSince sales={salesSince}/>
        </div>)
    }
}
export default Charts;
