    render() {
        const monthNames = ["January", "February", "March", "April", "May", "June",
            "July", "August", "September", "October", "November", "December"
        ]
        let today = new Date()

        let sales = (
            <ResponsiveContainer width='100%' height={300}><LineChart data={this.state.sales} >
                <Line dataKey="sales" stroke="#8884d8" lineSize={30} dot={true}/>
                <XAxis  dataKey="month" />
                <YAxis dataKey='sales'/>
            </LineChart></ResponsiveContainer>)

        return (<div>
            Sales since  {monthNames[today.getMonth() ]} {today.getFullYear()}
            {sales}
        </div>)
    }
}
export default SalesSince;
