import React, { useState } from 'react';
import './header.css'
import './burger.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'

function HeaderMenu() {
    const [burger, setBurger] = useState(false)

    const [burgerState, setBurgerState] = useState("")
    const [menuState, setMenuState] = useState("MENÚ")
    const [container, setContainer] = useState("")

    const [focus, setFocus] = useState(false)
    const [blur, setBlur] = useState(true)


    function focusFunc(boolean) {
        if (boolean) {
            setFocus(true)
            setContainer("-search")
        }
        else {
            setFocus(false)
            setContainer("")
        }
    }

    function openBurger() {
        setBurger(!burger)
        if (burger) {
            setBurgerState("open")
            setMenuState("CERRAR")
        }
        else {
            setBurgerState("")
            setMenuState("MENÚ")
        }


    }



    return (


        <div id="header-menu">
            <div id="burger-cont" onClick={() => this.props.openBurger()}>
                <div id="nav-icon4" className={this.props.burgerState}>  <span></span>  <span></span> <span></span></div>
                <span id="header-menu-title">{this.props.menuState}</span>
            </div>
            <div id="search-bar-cont">
                <input id="search" type="text" placeholder="Buscar" onFocus={() => { this.props.focusFunc(true) }} onBlur={() => this.props.focusFunc(false)} />
                {!this.props.focus ? <FontAwesomeIcon class="searchIcon" icon={faSearch} /> : null}

                <button id={`buscar-butt${this.props.container}`}>BUSCAR</button>
            </div>
        </div>


    )
}


export default HeaderMenu;
