import './App.css';
import NavBar from './components/NavBar';


// import { BrowserRouter as Router, Route} from 'react-router-dom'
import Clients from './components/Clients';

class App extends Component{

  constructor(){
    super()
    this.state = {

    }
  }

  async componentDidMount(){
    await setTimeout( () => {
      let data = require('./data.json')
      this.setState({data: data}) 
      console.log(data)

    }, 1000)

  }

  render(){



    return (
      <div>

      {/* <NavBar/> */}
      <Router>


      <Route exact path="/clients" render={() => <Clients clients={this.state.data}/>} />
      <Route exact path="/actions" render={() => <Clients clients={this.state.data}/>} />
      <Route exact path="/clients" render={() => <Clients clients={this.state.data}/>} />

    </Router>
    </div>
  )
}
  }


export default App;
