import React, { Component } from 'react';
import axios from 'axios';


class UpdateClient extends Component {
    constructor() {
        super()
        this.state = {
            owners: [],
            clients: [],
            name: "",
            transfer: "",
            email: ""
        }
    }

    componentDidMount() {
        let ownersobj = {}
        this.props.clients.forEach(d => {
            if (!ownersobj[d.owner]) {
                this.state.owners.push(d.owner)
                ownersobj[d.owner] = true

            }
        })

        this.props.clients.forEach(d => {
                this.state.clients.push({name: d.name, value: d.name})
        })

    }

    update = async (event) => {
        await this.setState({
            [event.target.name]: event.target.value,
        })
    }

    transfer = () => {
        axios.put(`http://localhost:5000/updateowner/${this.state.name}`, ({ owner: this.state.owner }))
    }
    sendEmail = () => {
        axios.put(`http://localhost:5000/sendemail/${this.state.name}`, ({ email: this.state.email }))
    }
    sell = () => {
        axios.put(`http://localhost:5000/sell/${this.state.name}`)
    }




    render() {

        return (<div>
            UPDATE CLIENT
            {/* look for client */}
            <div>Client: <input type="text" name="name" value={this.state.name} onChange={this.update} placeholder="Client name" /></div>
            <div>Client 

                 <select onChange={this.update} name="name" value={this.state.transfer}>
                    {this.state.owners.map(o => <option value={o}>{o}</option>)}

                </select>
            </div>

            {/* transfer client */}
            <div>Trasfer ownership to:
                 <select onChange={this.update} name="transfer" value={this.state.transfer}>
                    {this.state.owners.map(o => <option value={o}>{o}</option>)}

                </select>
                <button onClick={this.transfer} >Transfer</button>
            </div>

            {/* send an email to client */}
            <div>Send email:
                    <select onChange={this.update} name="email" value={this.state.email}>
                    <option value="A">A</option>
                    <option value="B">B</option>
                    <option value="C">C</option>
                    <option value="D">D</option>
                </select>

                <button onClick={this.sendEmail}>Send</button>
            </div>

            {/* declare sale */}
            <div>Declare sale <button onClick={this.sell}>Declare</button></div>

        </div>)
    }

}

export default UpdateClient;
