import React, { Component } from 'react';
class NewClients extends Component {
    render() {
        const monthNames = ["January", "February", "March", "April", "May", "June",
            "July", "August", "September", "October", "November", "December"
        ]
        let counter = 0
        let today = new Date()
        let date = 2018 + '-' + (today.getMonth() + 1) + '-' + 4 // year at 2017 cause data is old
        this.props.clients.forEach(c => c.firstContact < date ? null : counter += 1)


    return (<div>New clients in {monthNames[today.getMonth()]}: {counter}</div>)
    }
}
export default NewClients;
