import React, { Component } from 'react';
import { LineChart, Line } from 'recharts';
import {  Tooltip, Legend, CartesianGrid, XAxis, YAxis, ResponsiveContainer } from 'recharts';



class SalesSince extends Component {
    constructor(props) {
        super(props)
        //harcodeado
        this.state = {
            sales: [
                {month: "Jan", sales:20},
                {month: "Feb", sales:30},
                {month: 'Mar', sales:25},
                {month: 'Apr', sales:32},
                {month: 'May', sales:39},
                {month: 'Jun', sales:42},
                {month: 'Jul', sales:60},
                {month: 'Aug', sales:63},
                {month: 'Sep', sales:59},
                {month: 'Oct', sales:65},
                {month: 'Nov', sales:50},
                {month: "Dic", sales:43},
            ]
        }

    }


    // UNSAFE_componentWillReceiveProps(nextProps) {
    //     // Any time props.email changes, update state.
    //     if (nextProps.sales !== this.props.sales) {
    //       this.setState({
    //         sales: nextProps.sales
    //       });
    //     }
    //   }

   formatArray(){
    let today = new Date()

    let sales = [...this.state.sales]
    let ordered = sales.splice(today.getMonth())

    sales.forEach(a=> ordered.push(a))


    return ordered
 }

//  arr.unshift()
    render() {
        const monthNames = ["January", "February", "March", "April", "May", "June",
            "July", "August", "September", "October", "November", "December"
        ]
        let today = new Date()

        let data = this.formatArray()
        let sales = (
            <ResponsiveContainer width='100%' height={300}><LineChart data={data} dot={false} >
                <Line type="monotone" dataKey="sales" stroke="#8884d8" lineSize={30} />
                <XAxis  dataKey="month" />
                <YAxis dataKey='sales'/>
            </LineChart></ResponsiveContainer>)

        return (<div>
            Sales since  {monthNames[today.getMonth() ]} {(today.getFullYear() - 1)}
            {sales}
        </div>)
    }
}
export default SalesSince;
